import { client } from "../config";

const route = '/rota/exemplo';

export async function associate(codigo) {
  // Simulação de consulta ao banco de dados
  return new Promise((resolve) => {
    setTimeout(() => {
      //const response = await client.post(`${route}/:codigo`);
      // response.status === 200
      if (codigo === '1234') {
        resolve(true); // Código válido
        //const getMac = response.data.mac;
      } else {
        resolve(false); // Código inválido
      }
    }, 1000);
  });
}

// no lubar de codigo aqui deve salvar o getMac
export async function salveAssociate(codigo, serial, iccid) {
  console.log("Botão clicado!");
  console.log(codigo, serial, iccid);
  const data = {
    codigo,
    serial,
    iccid,
  };
  // const response =
  await client.post(`${route}/create`, data);
  const status = 200;
  return status;
}
